import React, { useState } from "react";

function Brockerage() {
  const [activeTab, setActiveTab] = useState("equity");

  const renderTable = () => {
    const tableStyle = {
      width: "100%",
      borderCollapse: "separate",
      borderSpacing: 0,
      border: "1px solid #dee2e6",
      borderRadius: "10px",
      overflow: "hidden",
      marginTop: "20px",
      boxShadow: "0px 2px 6px rgba(0, 0, 0, 0.08)",
    };

    const thStyle = {
      backgroundColor: "#f8f9fa",
      borderBottom: "2px solid #dee2e6",
      textAlign: "center",
      fontWeight: "600",
      padding: "12px",
    };

    const tdStyle = {
      textAlign: "center",
      padding: "10px",
      verticalAlign: "middle",
      color: "#333",
    };

    const rowStyle = (index) => ({
      backgroundColor: index % 2 === 0 ? "#f1f7ff" : "#ffffff",
    });

    switch (activeTab) {
      case "equity":
        return (
          <table style={tableStyle}>
            <thead>
              <tr>
                <th style={thStyle}></th>
                <th style={thStyle}>Equity delivery</th>
                <th style={thStyle}>Equity intraday</th>
                <th style={thStyle}>F&O - Futures</th>
                <th style={thStyle}>F&O - Options</th>
              </tr>
            </thead>
            <tbody>
              {[
                [
                  "Brokerage",
                  "Zero Brokerage",
                  "0.03% or Rs. 20/executed order whichever is lower",
                  "0.03% or Rs. 20/executed order whichever is lower",
                  "Flat Rs. 20 per executed order",
                ],
                [
                  "STT/CTT",
                  "0.1% on buy & sell",
                  "0.025% on the sell side",
                  "0.02% on the sell side",
                  <>
                    <ul style={{ textAlign: "left", margin: "0", paddingLeft: "20px" }}>
                      <li>0.125% of intrinsic value on exercised options</li>
                      <li>0.1% on sell side (on premium)</li>
                    </ul>
                  </>,
                ],
                [
                  "Transaction charges",
                  <>
                    NSE: 0.00297% <br /> BSE: 0.00375%
                  </>,
                  <>
                    NSE: 0.00297% <br /> BSE: 0.00375%
                  </>,
                  <>
                    NSE: 0.00173% <br /> BSE: 0
                  </>,
                  <>
                    NSE: 0.03503% (on premium) <br /> BSE: 0.0325% (on premium)
                  </>,
                ],
                [
                  "GST",
                  "18% on (brokerage + SEBI charges + transaction charges)",
                  "18% on (brokerage + SEBI charges + transaction charges)",
                  "18% on (brokerage + SEBI charges + transaction charges)",
                  "18% on (brokerage + SEBI charges + transaction charges)",
                ],
                ["SEBI charges", "₹10 / crore", "₹10 / crore", "₹10 / crore", "₹10 / crore"],
                [
                  "Stamp charges",
                  "0.015% or ₹1500 / crore on buy side",
                  "0.003% or ₹300 / crore on buy side",
                  "0.002% or ₹200 / crore on buy side",
                  "0.003% or ₹300 / crore on buy side",
                ],
              ].map((row, i) => (
                <tr key={i} style={rowStyle(i)}>
                  {row.map((col, j) => (
                    <td key={j} style={tdStyle}>
                      {col}
                    </td>
                  ))}
                </tr>
              ))}
            </tbody>
          </table>
        );

      case "currency":
        return (
          <table style={tableStyle}>
            <thead>
              <tr>
                <th style={thStyle}></th>
                <th style={thStyle}>Currency Futures</th>
                <th style={thStyle}>Currency Options</th>
              </tr>
            </thead>
            <tbody>
              {[
                ["Brokerage", "0.03% or ₹20/executed order", "₹20/executed order"],
                ["STT/CTT", "No STT", "No STT"],
                [
                  "Transaction charges",
                  <>
                    NSE: 0.00035% <br /> BSE: 0.00055%
                  </>,
                  <>
                    NSE: 0.0311% <br /> BSE: 0.001%
                  </>,
                ],
                [
                  "GST",
                  "18% on (brokerage + SEBI charges + transaction charges)",
                  "18% on (brokerage + SEBI charges + transaction charges)",
                ],
                ["SEBI charges", "₹10 / crore", "₹10 / crore"],
                [
                  "Stamp charges",
                  "0.0001% or ₹10 / crore on buy side",
                  "0.0001% or ₹10 / crore on buy side",
                ],
              ].map((row, i) => (
                <tr key={i} style={rowStyle(i)}>
                  {row.map((col, j) => (
                    <td key={j} style={tdStyle}>
                      {col}
                    </td>
                  ))}
                </tr>
              ))}
            </tbody>
          </table>
        );

      case "commodity":
        return (
          <table style={tableStyle}>
            <thead>
              <tr>
                <th style={thStyle}></th>
                <th style={thStyle}>Commodity Futures</th>
                <th style={thStyle}>Commodity Options</th>
              </tr>
            </thead>
            <tbody>
              {[
                ["Brokerage", "0.03% or ₹20/executed order", "₹20/executed order"],
                ["STT/CTT", "0.01% on sell side (non-agri)", "0.05% on sell side"],
                [
                  "Transaction charges",
                  <>
                    MCX: 0.0027% <br /> NSE: 0.001%
                  </>,
                  <>
                    MCX: 0.0418% <br /> NSE: 0.001%
                  </>,
                ],
                [
                  "GST",
                  "18% on (brokerage + SEBI charges + transaction charges)",
                  "18% on (brokerage + SEBI charges + transaction charges)",
                ],
                [
                  "SEBI charges",
                  <>
                    Agri: ₹1 / crore <br /> Non-agri: ₹10 / crore
                  </>,
                  "₹10 / crore",
                ],
                [
                  "Stamp charges",
                  "0.002% or ₹200 / crore on buy side",
                  "0.003% or ₹300 / crore on buy side",
                ],
              ].map((row, i) => (
                <tr key={i} style={rowStyle(i)}>
                  {row.map((col, j) => (
                    <td key={j} style={tdStyle}>
                      {col}
                    </td>
                  ))}
                </tr>
              ))}
            </tbody>
          </table>
        );

      default:
        return null;
    }
  };

  return (
    <div className="container mt-5">
      <div
        style={{
          display: "flex",
          justifyContent: "center",
          gap: "10px",
          marginBottom: "20px",
        }}
      >
        {["equity", "currency", "commodity"].map((tab) => (
          <button
            key={tab}
            onClick={() => setActiveTab(tab)}
            style={{
              padding: "10px 25px",
              borderRadius: "0",
              fontWeight: 500,
              fontSize: "16px",
              border: activeTab === tab ? "2px solid #007bff" : "1px solid #007bff",
              backgroundColor: activeTab === tab ? "#007bff" : "transparent",
              color: activeTab === tab ? "white" : "#007bff",
              cursor: "pointer",
              transition: "all 0.2s ease-in-out",
            }}
          >
            {tab.charAt(0).toUpperCase() + tab.slice(1)}
          </button>
        ))}
      </div>

      {renderTable()}
    </div>
  );
}

export default Brockerage;
